import React,{Component} from 'react'
import { BrowserRouter as Router, Route, Routes,Link } from 'react-router-dom'
import withNavigation from './WithNavigation.jsx' 
import withParams from './WithParams.jsx'
import AuthenticationServices from './AuthenticationServices.js'

class LoginApp extends Component{
    render(){
        const FloginWithNavigation = withNavigation(Flogin);
        const WelcomePageWithParams =withParams(WelcomePage);
        return(

            <div className="LoginApp">
                <Router>
                <HeaderComponent/>
                
                <Routes>  
                    <> 
                    
                           <Route path="/"  element component={<FloginWithNavigation/>}/>
                            <Route path="/login" element={<FloginWithNavigation />}/>
                           <Route path="/welcome/:name" element={<WelcomePageWithParams/>}/>
                           <Route path="/list" element={<ListComponent/>}/>
                           <Route path="/logout" element={<LogoutComponent/>}/>
                           <Route path="*"element={<ErrorComponent/>}/>
                           
                    </>
                </Routes>
                <FooterComponent/>
                </Router>

{/*<Flogin/>*/}
{/*<WelcomePage/>*/}
            </div>
        )
    }
}
class HeaderComponent extends Component{
    render(){
        return(
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a href="https:/wwww.Fullstackdev.com" className="navbar-brand">Fullstackdev</a></div>
                    <ul className="navbar-nav navbar-collapse content-justify end">
                        <li className="nav-link"> <Link to="/welcome/djany">Home</Link></li>
                         <li className="nav-link"><Link to="/list">List</Link></li>

                    </ul>
                    <ul className="navbar-nav">
                        <li className="nav-link"><Link to="/login">Login</Link></li>
                         <li className="nav-link"><Link to="/logout">Logout</Link></li>

                    </ul>




                </nav>

            </header>
        )
    }
}
class FooterComponent extends Component{
    render(){
        return(
            <footer className="footer">
                <span className="text-muted">all Right reserved 2022</span>


            </footer>
        )
    }
}
class LogoutComponent extends Component{
    render(){
        return(
            <>
            <h1>you are logout</h1>

            <div className="container">
                Thank for using our Application

            </div>
            </>
        )
    }
}
function ErrorComponent() {
    return <div>An Error Occurred. I don't know what to do! Contact support at abcd-efgh-ijkl</div>
}

class WelcomePage extends Component{
    render(){
        return(
            <>
            <h1>Welcome!</h1>
            <div className="container">
 <div>Welcome  {this.props.params.name}. you can see the list <Link to="/list">here</Link>.</div>

            </div>
            
            </>
        )
    
    
    }
}
class ListComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            
            lists:
            [

            

             {id : 1,description:'learning React' ,done:false,targetDate:new Date()},
             {id : 2,description:'I CAN' ,done:false,targetDate:new Date()},
             {id : 3,description:'i WILL GET IT' ,done:false,targetDate:new Date()}
            ]

        }
    }
    render(){
        return (
        
        <div>
            <h1>List Component</h1>

        <div className="container">
            <table className="table">
                <thead>
                    <tr>
                        <th>id</th>
                        <th>description</th>
                        <th>targetDate</th>
                        <th>isCompleted</th>
                    </tr>
                    </thead>

                <tbody>
                    {
             this.state.lists.map(
                 list =>
                 <tr>
                 <td>{list.id}</td>
                <td>{list.description}</td>
                <td>{list.done.toString()}</td>
                <td>{list.targetDate.toString()}</td>
                </tr>
             )
                    }
                    </tbody>
            </table></div></div>
        
    )
    }
}




 
class Flogin extends Component{
    constructor(props){
        super(props)
        this.state={
            username:'djany',
            password:'',
            hasLoginFailed: false ,
            showSuccessMessage: false
        }
//this.handleUsernameChange = this.handleUsernameChange.bind(this)
//this.handlePasswordChange = this.handlePasswordChange.bind(this)
this.handleChange = this.handleChange.bind(this)
this.loginClicked = this.loginClicked.bind(this)
    }
    handleChange(event){
        console.log(this.state);
        this.setState(
            {
            [event.target.name]
            :event.target.value
        }
        )

    }
  //  handleUsernameChange(event){
    //    console.log(event.target.name);
