import React,{Component} from 'react'
import { BrowserRouter as Router, Route, Routes,Link } from 'react-router-dom'
import withNavigation from './WithNavigation.jsx' 
import withParams from './WithParams.jsx'
import AuthenticationServices from './AuthenticationServices.js'
import AuthenticatedRoute from './AuthenticatedRoute.jsx'
import './footer.css';
import Flogin from './Flogin.jsx'
import ListComponent from './ListComponent.jsx'


class LoginApp extends Component{
    render(){
        const FloginWithNavigation = withNavigation(Flogin);
        const WelcomePageWithParams =withParams(WelcomePage);
        const HeaderComponentWithNavigation = withNavigation(HeaderComponent);
        return(

            <div className="LoginApp">
                <Router>
                <HeaderComponentWithNavigation/>
                
                <Routes>  
                    <> 
                    
                           <Route path="/"  element component={<FloginWithNavigation/>}/>
                            <Route path="/login" element={<FloginWithNavigation />}/> 


                            <Route path="/welcome/:name" element={<AuthenticatedRoute><WelcomePageWithParams /></AuthenticatedRoute>} />

                          <Route path="/list" element={<AuthenticatedRoute><ListComponent /></AuthenticatedRoute>} />


                          <Route path="/logout" element={<AuthenticatedRoute> <LogoutComponent /></AuthenticatedRoute>} />


                           <Route path="*"element={<ErrorComponent/>}/>
                           
                    </>
                </Routes>
                <FooterComponent/>
                </Router>

{/*<Flogin/>*/}
{/*<WelcomePage/>*/}
            </div>
        )
    }
}
class HeaderComponent extends Component{
    render(){
        const isUserLoggedIn=AuthenticationServices.isUserLoggedIn();
       //console.log(isUserLoggedIn);


        
        return(
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a href="https:/wwww.Fullstackdev.com" className="navbar-brand">Fullstackdev</a></div>
                    <ul className="navbar-nav">
                         {isUserLoggedIn && <li><Link className="nav-link" to="/welcome/djany">Home</Link></li>}
                          {isUserLoggedIn && <li><Link className="nav-link" to="/list">List</Link></li>}

                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                         {!isUserLoggedIn && <li><Link className="nav-link" to="/login">Login</Link></li>}
                         { isUserLoggedIn && <li><Link className="nav-link" to="/logout" onClick={AuthenticationServices.logout}>Logout</Link></li>}

                    </ul>




                </nav>

            </header>
        )
    }
}
class FooterComponent extends Component{
    render(){
        return(
            <footer className="footer">
                <span className="text-muted">all Right reserved 2022</span>


            </footer>
        )
    }
}
class LogoutComponent extends Component{
    render(){
        return(
            <>
            <h1>you are logout</h1>

            <div className="container">
                Thank for using our Application

            </div>
            </>
        )
    }
}
function ErrorComponent() {
    return <div>An Error Occurred. I don't know what to do! Contact support at abcd-efgh-ijkl</div>
}

class WelcomePage extends Component{
    render(){
        return(
            <>
            <h1>Welcome!</h1>
            <div className="container">
 <div>Welcome  {this.props.params.name}. you can see the list <Link to="/list">here</Link>.</div>

            </div>
            
            </>
        )
    
    
    }
}

 
export default LoginApp
