import  React, { Component } from 'react'
import { Navigate } from 'react-router-dom'
import AuthenticationServices from './AuthenticationServices.js'





class HeaderComponent extends Component{
    render(){
        const isUserLoggedIn=AuthenticationServices.isUserLoggedIn();
       //console.log(isUserLoggedIn);


        
        return(
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a href="https:/wwww.Fullstackdev.com" className="navbar-brand">Fullstackdev</a></div>
                    <ul className="navbar-nav">
                         {isUserLoggedIn && <li><Link className="nav-link" to="/welcome/djany">Home</Link></li>}
                          {isUserLoggedIn && <li><Link className="nav-link" to="/list">List</Link></li>}

                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                         {!isUserLoggedIn && <li><Link className="nav-link" to="/login">Login</Link></li>}
                         { isUserLoggedIn && <li><Link className="nav-link" to="/logout" onClick={AuthenticationServices.logout}>Logout</Link></li>}

                    </ul>




                </nav>

            </header>
        )
    }
}
class FooterComponent extends Component{
    render(){
        return(
            <footer className="footer">
                <span className="text-muted">all Right reserved 2022</span>


            </footer>
        )
    }
}
class LogoutComponent extends Component{
    render(){
        return(
            <>
            <h1>you are logout</h1>

            <div className="container">
                Thank for using our Application

            </div>
            </>
        )
    }
}
function ErrorComponent() {
    return <div>An Error Occurred. I don't know what to do! Contact support at abcd-efgh-ijkl</div>
}

class WelcomePage extends Component{
    render(){
        return(
            <>
            <h1>Welcome!</h1>
            <div className="container">
 <div>Welcome  {this.props.params.name}. you can see the list <Link to="/list">here</Link>.</div>

            </div>
            
            </>
        )
    
    
    }
}
class ListComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            
            lists:
            [

            

             {id : 1,description:'learning React' ,done:false,targetDate:new Date()},
             {id : 2,description:'I CAN' ,done:false,targetDate:new Date()},
             {id : 3,description:'i WILL GET IT' ,done:false,targetDate:new Date()}
            ]

        }
    }
    render(){
        return (
        
        <div>
            <h1>List Component</h1>

        <div className="container">
            <table className="table">
                <thead>
                    <tr>
                        <th>id</th>
                        <th>description</th>
                        <th>targetDate</th>
                        <th>isCompleted</th>
                    </tr>
                    </thead>

                <tbody>
                    {
             this.state.lists.map(
                 list =>
                 <tr key={list.id}>
                 <td>{list.id}</td>
                <td>{list.description}</td>
                <td>{list.done.toString()}</td>
                <td>{list.targetDate.toString()}</td>
                </tr>
             )
                    }
                    </tbody>
            </table></div></div>
        
    )
    }
}




 
class Flogin extends Component{
    constructor(props){
        super(props)
        this.state={
            username:'djany',
            password:'',
            hasLoginFailed: false ,
            showSuccessMessage: false
        }
//this.handleUsernameChange = this.handleUsernameChange.bind(this)
//this.handlePasswordChange = this.handlePasswordChange.bind(this)
this.handleChange = this.handleChange.bind(this)
this.loginClicked = this.loginClicked.bind(this)
    }
    handleChange(event){
        console.log(this.state);
        this.setState(
            {
            [event.target.name]
            :event.target.value
        }
        )

    }
  //  handleUsernameChange(event){
    //    console.log(event.target.name);
    //    this.setState(
     //       {
     //       [event.target.name]
     //       :event.target.value
    //    }
    //    )

   // }
   // handlePasswordChange(event){
    //    console.log(event.target.value);
     //   this.setState({password:event.target.value})

  //  }
    loginClicked(){
        if(this.state.username==='djany' && this.state.password==='1234'){ 
            AuthenticationServices.registerSuccessfulLogin(this.state.username,this.state.password)
            this.props.navigate(`/welcome/${this.state.username}`)
        //this.props.history.push("/welcome")
      //  console.log('succefully')
        //this.setState({showSuccessMessage:true})
       // this.setState({hasLoginFailed:false})
        }
        else {
            //console.log('failed')
      this.setState({showSuccessMessage:false})

       this.setState({hasLoginFailed:true})
        }
        //console.log('failed')
        console.log(this.state)
    }
    
    render(){
        return(
            <div>
            <h1>Login Page</h1>
             
            <div className="container">
           
              {/*<ShowInvalidCredential hasLoginFailed={this.state.hasLoginFailed}/>*/}
              {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credential</div>}
              { /*<ShowMessageSuccessfully  showSuccessMessage={this.state.showSuccessMessage}/> */}
              {this.state.showSuccessMessage && <div>Login succefully</div>}
UserName:<input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>

 
Password:<input type="password" name="password"value={this.state.password} onChange={this.handleChange}/>
<button className="btn btn" onClick={this.loginClicked}>Login</button>
</div></div>

        )
    }
}
